mapper.update:
update help_table
	set 
  		title= #{title} , content= #{content} , points= #{points} , location=#{location} 
	where 
  		id=#{id}

  	update help_table
		set views = views + 1
		where id = #{id}

mapper.select:
select count(*) 
from help_like_table 
where help_id=#{help_id} and user_id=#{user_id}

mapper.update:
	update help_comment_table
    	set status='채택 완료'
        	where id=#{id}

update help_table
	set status='채택 완료'
		where id=#{id}
