mapper.update:
update question_table
set question_category_id=#{question_category_id}, title= #{title} , content=#{content}  , points= #{points}
where id=#{id}

update question_table
set views = views + 1
where id = #{id}

mapper.select:
select count(*)
from question_like_table
where question_id=#{question_id} and user_id=#{user_id}
